    public static ArrayList<Food> getListData(){
        ArrayList<Food> list = new ArrayList<>();
        for (String[] aData : data){
            Food food = new Food();
            food.setJudul(aData[0]);
            food.setDeskripsi(aData[1]);
            food.setFoto(aData[2]);
            food.setDesc(aData[3]);
            food.setBahan(aData[4]);
            food.setMake(aData[5]);
            food.setPrice(aData[6]);

            list.add(food);
        }
        return list;
    }
